CREATE TABLE BUILDING
( 
  BUILDING_ID VARCHAR(4) PRIMARY KEY,
  BUILDING_NAME VARCHAR(40),
  LOCATION_POINTS SDO_GEOMETRY
);
CREATE TABLE STUDENTS
(
  STUDENT_ID VARCHAR(4) PRIMARY KEY,
  STUDENT_POINTS SDO_GEOMETRY
);

CREATE TABLE TRAMSTOPS
( 
  STOP_NAME VARCHAR(10) PRIMARY KEY,
  TRAM_POINTS SDO_GEOMETRY
);









